'''
The appeal of a string is the number of distinct characters found in the string.

For example, the appeal of "abbca" is 3 because it has 3 distinct characters: 'a', 'b', and 'c'.
Given a string s, return the total appeal of all of its substrings.

A substring is a contiguous sequence of characters within a string.
'''

from timeit import default_timer as timer
from datetime import timedelta
from typing import List, Tuple, Optional, Set

class Solution:
    def __init__(self):
        self.memo = {}
        
    def __prefixAppealSum(self, s: str, sLen: int, indent=''):
        aSum = 0
        uniqC = set({})
        for i in range(len(s)):
            uniqC.add(s[i])
            tmp = len(uniqC)
            #self.memo[s[:i+1]] = tmp
            #print('{}aSum[{}] = {}'.format(indent, s[:i+1], tmp))
            aSum += tmp
        return aSum
    
    def __appealSum(self, s: str, sLen: int, indent='') -> int:
        aSum = 0
        sLen = 0
        for i in range(len(s) - 1, -1, -1):
            sLen += 1
            aSum += self.__prefixAppealSum(s[i:], sLen)

        return aSum
            
    
    def appealSum(self, s: str) -> int:
        return self.__appealSum(s, len(s))

if __name__ == "__main__":
    soln = Solution()
    inputs = ["abbca", "code",
              ""
]

    for s in inputs:
        start = timer()
        maxAppeal = soln.appealSum(s)
        end = timer()
        print('maxAppeal for \'{}\' is {}; found in {} time'.format(s, maxAppeal, timedelta(seconds=end - start)))

# Expected result for the big string is 6553101476
